    public abstract boolean sendNotification(UserEntity user, HostEntity host);

    public abstract byte getActive();

    public static void notifyAllUsers(HostEntity host) {
        notificationProviders = DBUtil.getNotificationProviders();

        for (NotificationProvider notificationProvider : notificationProviders) {
            if (notificationProvider.getActive() == 1) {
                for (UserEntity user : DBUtil.getUsers()) {
                    if (user.getIsActive() == 1)
                        notificationProvider.sendNotification(user, host);
                }
            }
        }
    }

    public static boolean checkValid(DataType type, String data) {
        if (data == null) return false;
        return data.matches(validationTemplates.get(type));
    }
